mapper.select:
SELECT 

FROM diy_guide_term a

WHERE a.id = #{id}

SELECT 

FROM diy_guide_term a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.book_id = #{book.id}


		AND a.section_id = #{section.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM diy_guide_term a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE diy_guide_term SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
