mapper.select:
SELECT 

FROM int_guide_term_item a

WHERE a.id = #{id}

SELECT 

FROM int_guide_term_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM int_guide_term_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE int_guide_term_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
