mapper.select:
SELECT 

FROM mod_proposal_section a

WHERE a.id = #{id}

SELECT 

FROM mod_proposal_section a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.scheme_id = #{scheme.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_proposal_section a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_proposal_section SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
