mapper.select:
SELECT 

FROM diy_solution_item a

WHERE a.id = #{id}

SELECT 

FROM diy_solution_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.solution_id = #{solution.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}



		ORDER BY a.priority

SELECT 

FROM diy_solution_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE diy_solution_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
