mapper.select:
SELECT 

FROM mod_badge a

WHERE a.id = #{id}

SELECT 

FROM mod_badge a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.key = #{key}




		ORDER BY ${page.orderBy}


		ORDER BY a.level

SELECT 

FROM mod_badge a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.level

mapper.update:
UPDATE mod_badge SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
