mapper.select:
SELECT 

FROM mod_board a

WHERE a.id = #{id}

SELECT 

FROM mod_board a


	a.del_flag = 0
	AND a.broker_id = #{brokerId} 

ORDER BY a.update_date DESC 
limit #{offset},#{size}

SELECT
FROM mod_board a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM mod_board a


	a.del_flag = #{DEL_FLAG_NORMAL} 
	and a.update_date > #{startDate} 
	and a.update_date < #{endDate} 

ORDER BY a.update_date DESC 
limit #{offset},#{size}

SELECT 

FROM mod_board a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_board SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
