mapper.select:
SELECT 

FROM mod_board_item a

WHERE a.id = #{id}

SELECT
FROM mod_board_item a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM mod_board_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_board_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
