mapper.select:
SELECT 

FROM mod_broker a

WHERE a.id = #{id}

SELECT 

FROM mod_broker a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.parent_id = #{parent.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_broker a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_broker SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
