mapper.select:
SELECT 

FROM mod_channel a

WHERE a.id = #{id}

SELECT
FROM mod_channel a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.code = #{code}
AND a.tagging LIKE '%'||#{tagging}||'%' '%'+#{tagging}+'%' concat('%',#{tagging},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM mod_channel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_channel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
