mapper.select:
SELECT 

FROM mod_cps_scheme a

WHERE a.id = #{id}

SELECT 

FROM mod_cps_scheme a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.platform = #{platform}

LIMIT 1

SELECT 

FROM mod_cps_scheme a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_cps_scheme a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_cps_scheme SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
