mapper.select:
SELECT 

FROM mod_dict_meta a

WHERE a.id = #{id}

SELECT 

FROM mod_dict_meta a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type = #{type}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_dict_meta a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_dict_meta SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
