mapper.select:
SELECT 

FROM mod_payment a

WHERE a.id = #{id}

SELECT 

FROM mod_payment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_payment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_payment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
