mapper.select:
SELECT 

FROM mod_persona a

WHERE a.id = #{id}

SELECT 

FROM mod_persona a

WHERE a.parent_id = #{parentId}

SELECT 

FROM mod_persona a

WHERE a.parent_id = "0" and a.phase_id=#{phaseId}

SELECT 

FROM mod_persona a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.parent_id = #{parent.id}


		AND a.phase_id = #{phase.id}


		AND a.hierarchy_id = #{hierarchy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_persona a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_persona a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.parent_ids LIKE #{parentIds}

order by a.create_by ASC

mapper.update:
UPDATE mod_persona SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
