mapper.select:
SELECT 

FROM mod_phase a

WHERE a.id = #{id}

SELECT 

FROM mod_phase a

WHERE a.parent_id = #{parentId}

SELECT 

FROM mod_phase a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')

	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.sort ASC

SELECT 

FROM mod_phase a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.sort ASC

SELECT 

FROM mod_phase a


	a.del_flag = #{DEL_FLAG_NORMAL} 
	AND a.parent_ids LIKE #{parentIds}


order by a.sort

mapper.update:
UPDATE mod_phase SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
