mapper.select:
SELECT 

FROM mod_platform_source a

WHERE a.id = #{id}

SELECT 

FROM mod_platform_source a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.platform = #{platform}


		AND a.type = #{type}


		AND a.status = #{status}


ORDER BY a.sort DESC

SELECT 

FROM mod_platform_source a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.platform = #{platform}


		AND a.type = #{type}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_platform_source a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
