mapper.select:
SELECT 

FROM mod_rank a

WHERE a.id = #{id}

SELECT
FROM mod_rank a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.category_id = #{category.id}
AND a.openid = #{openid}
AND a.nickname LIKE '%'||#{nickname}||'%' '%'+#{nickname}+'%' concat('%',#{nickname},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM mod_rank a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_rank SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
