mapper.select:
SELECT 

FROM mod_tag a

WHERE a.id = #{id}

SELECT 

FROM mod_tag a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.tag_category =#{tagCategory.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_tag a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_tag SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
