mapper.select:
SELECT a.id AS "id", a.category_id AS "category.id", c.name AS "category.name", a.name AS "name", a.type AS "type", a.`condition` AS "condition", a.expression AS "expression", 100*a.priority+(length(c.parent_ids)-length(replace(c.parent_ids,",","")) ) AS "priority", a.status AS "status", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.del_flag AS "delFlag", a.condition_desc AS "conditionDesc", a.description AS "description" FROM mod_template a, mod_item_category c
a.del_flag = 0 AND a.type = 'item'
AND a.status = #{status}
AND c.id = #{categoryId}
and (a.category_id=c.id or find_in_set(a.category_id,c.parent_ids)>0 )
ORDER BY priority DESC

SELECT 

FROM mod_template a

WHERE a.id = #{id}

SELECT 

FROM mod_template a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}


		AND a.category_id = #{category.id}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.priority DESC

SELECT 

FROM mod_template a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
FROM mod_template a
a.del_flag = '0' AND a.category_id = #{category}

mapper.update:
UPDATE mod_template SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
