mapper.select:
SELECT 

FROM mod_user_measure a

WHERE a.id = #{id}

SELECT 

FROM mod_user_measure a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.category = #{category.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM mod_user_measure a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE mod_user_measure SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT
FROM mod_measure a
a.del_flag = '0' AND a.category = #{category}
