mapper.select:
SELECT 

FROM wx_group a

WHERE a.id = #{id}

SELECT 

FROM wx_group a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.broker_id = #{broker.id}


		AND a.persona_id = #{persona.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.owner = #{owner}


		AND a.gid = #{gid}


		AND a.token = #{token}


		AND a.status = #{status}


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wx_group a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_group SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
