mapper.select:
SELECT 

FROM wx_group_task a

WHERE a.id = #{id}

SELECT 

FROM wx_group_task a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.broker_id = #{broker.id}


		AND a.group_id = #{wxgroup.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wx_group_task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_group_task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
