mapper.select:
SELECT 

FROM wx_reads a

WHERE a.id = #{id}

SELECT 

FROM wx_reads a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.article_id = #{article.id}


		AND a.broker_id = #{broker.id}


		AND a.openid = #{openid}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wx_reads a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_reads SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
