mapper.select:
SELECT 

FROM wx_subscribes a

WHERE a.id = #{id}

SELECT 

FROM wx_subscribes a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account_id = #{account.id}


		AND a.broker_id = #{broker.id}


		AND a.openid = #{openid}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wx_subscribes a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_subscribes SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
